mapper.update:
update paragraph
set paracontent = #{paracontent,jdbcType=VARCHAR},
  paraindex = #{paraindex,jdbcType=INTEGER},
  document_id = #{documentId,jdbcType=INTEGER}
where pid = #{pid,jdbcType=INTEGER}

mapper.select:
select pid, paracontent, paraindex, document_id
from paragraph
where pid = #{pid,jdbcType=INTEGER}
