mapper.update:
update dt_instance
set user_id = #{userId,jdbcType=INTEGER},
  task_id = #{taskId,jdbcType=INTEGER},
  instance_id = #{instanceId,jdbcType=INTEGER}
where dt_instid = #{dtInstid,jdbcType=INTEGER}

mapper.select:
select dt_instid, user_id, task_id, instance_id
from dt_instance
where dt_instid = #{dtInstid,jdbcType=INTEGER}
