mapper.update:
update dt_relation
set dt_id = #{dtId,jdbcType=INTEGER},
  labeltype = #{labeltype,jdbcType=VARCHAR},
  label_id = #{labelId,jdbcType=INTEGER}
where dtd_id = #{dtdId,jdbcType=INTEGER}

mapper.select:
select dtd_id, dt_id, labeltype, label_id
from dt_relation
where dtd_id = #{dtdId,jdbcType=INTEGER}
